<script>
jQuery(document).ready(function($) {
    // Handle subscriber deletion
    $('#the-list').on('click', '.delete-subscriber', function(e) {
        e.preventDefault();
        if (!confirm('آیا از حذف این مشترک اطمینان دارید؟ این عمل قابل بازگشت نیست.')) {
            return;
        }
        
        const $link = $(this);
        const subId = $link.data('id');
        const nonce = $link.data('nonce');
        const $row = $('#subscriber-' + subId);

        $.post(ajaxurl, {
            action: 'wnw_delete_subscriber',
            id: subId,
            nonce: nonce
        })
        .done(function(response) {
            if (response.success) {
                $row.fadeOut(300, function() { $(this).remove(); });
                $('#wnw-ajax-message').html('<div class="notice notice-success is-dismissible"><p>' + response.data.message + '</p></div>').show();
            } else {
                $('#wnw-ajax-message').html('<div class="notice notice-error is-dismissible"><p>' + response.data.message + '</p></div>').show();
            }
        })
        .fail(function() {
            alert('خطا در ارتباط با سرور.');
        });
    });
});
</script>
